<script type="text/javascript">
	function fire_onetime_event(name) {
		var onetimeval = name.replace(/ /g, "-");
		if (inBetween(document.cookie, "cpevent-"+onetimeval+"=", ";") != "y")
		{
			var exp = new Date(); exp.setDate(exp.getDate()+700);
			document.cookie = "cpevent-"+onetimeval+"=y;expires="+exp.toUTCString();
			fire_usage_event("onetime wizard page");
		}
	}
	
	$(window).load(function () {
		fire_onetime_event("wizard page");
	});
</script>


