<script>
	var position = null;
	var fid="new";
    var step = Number(2);
	var currentStep = 2 ;
	if(step == 3 &&  fid == ''){
		$('.wizardSteps').remove();
	}else{
		if('' == '' && fid== 'new' ){
			$('ol a').each(function(i){
				if(i != 0)
				$(this).attr('href', $(this).attr('href') +'&step='+currentStep);
			})
		}
		
		
		$('.wizardSteps li:gt('+(step-1)+') a').each(function(){
			if(fid == 'new' ||  fid=='' || '' != ''){
					$(this).parent().removeClass('disable');
					$(this).removeAttr('href');
				}
		});
		
		if(fid != 'new' && fid != '' && '' == '' ){
			$('.wizardSteps li').removeClass();
		}else{
			if('' != ''){
				$('.wizardSteps li').eq(step).prevAll().removeClass();	
			}else{
				$('.wizardSteps li').eq(currentStep-1).prevAll().removeClass();	
			}	
		}
		
		$('.wizardSteps li').eq(currentStep-1).removeClass().addClass('active').children('a').removeAttr('href');
	}
	
	if( step == 4 ){
		$('.wizardSteps li').each(function(){
			if( $(this).index() == 1 ){
				$(this).attr("data-position","bottom");
				$(this).attr("data-intro", "Edit Gallery");
			}else if( $(this).index() == 2 ){
				$(this).attr("data-position","bottom");
				$(this).attr("data-intro", "Edit text, reorder or add more items");
			}
		});
	}
	
	$(window).scroll(function () {
		if (position == null && $('.wizardSteps').length)
			position = $('.wizardSteps').offset().top;
			var headerheight = $(".navAppMode").outerHeight();
		if (($(window).scrollTop() + headerheight) >= position) {
			$('.wizardSteps').addClass('fixed');
		} else {
			$('.wizardSteps').removeClass('fixed');
		}
	});
</script>

